<script setup lang="ts">
import { $t } from '@vben/locales';

// eslint-disable-next-line n/no-extraneous-import
import { Icon } from '@iconify/vue';
import { Card, CardMeta, Image, Tooltip } from 'ant-design-vue';

const props = defineProps({
  img: {
    type: String,
    required: true,
  },
  title: {
    type: String,
    required: true,
  },
  remark: {
    type: String,
    required: false,
    default: '',
  },
  id: {
    type: Number,
    required: true,
  },
  guid: {
    type: String,
    required: true,
  },
});

const emits = defineEmits(['click']);

/**
 * 点击卡片
 * @param type 点击类型
 */
function cardClick(type: 'delete' | 'edit' | 'preview' | 'view'): void {
  emits('click', {
    type,
    id: props.id,
    guid: props.guid,
  });
}
</script>

<template>
  <Card class="m-4 inline-block w-96">
    <template #cover>
      <Image
        width="100%"
        :height="200"
        :src="img"
        fallback="data:image/png;base64,"
      />
    </template>

    <template #actions>
      <!-- 预览 -->
      <div @click="cardClick('preview')">
        <Tooltip>
          <template #title>
            {{ $t('common.preview') }}
          </template>
          <Icon
            icon="mdi:link-variant"
            class="inline-block align-text-top text-xl"
          />
        </Tooltip>
      </div>
      <!-- 查看 -->
      <div @click="cardClick('view')">
        <Tooltip>
          <template #title>
            {{ $t('common.view') }}
          </template>
          <Icon icon="mdi:eye" class="inline-block align-text-top text-xl" />
        </Tooltip>
      </div>
      <!-- 编辑 -->
      <div @click="cardClick('edit')">
        <Tooltip>
          <template #title>
            {{ $t('common.edit') }}
          </template>
          <Icon icon="mdi:edit" class="inline-block align-text-top text-xl" />
        </Tooltip>
      </div>
      <!-- 删除 -->
      <div @click="cardClick('delete')">
        <Tooltip>
          <template #title>
            {{ $t('common.delete') }}
          </template>
          <Icon
            icon="mdi:delete"
            class="inline-block align-text-top text-xl text-red-500"
          />
        </Tooltip>
      </div>
    </template>
    <CardMeta :title="title" :description="remark" />
  </Card>
</template>

<style scoped></style>
